package cas_251106;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Stmpa 50 puta rijec informatika
		 */
		
		for(int i=0;i<50;i++) {
			System.out.println(i+" Informatika");
		}
		
		// i++   ->  i=i+1
		// i--   ->  i=i-1
		// i+=5  ->  i=i+5
		// i-=5  ->  i=i-5
		// i*=5  ->  i=i*5
		// i/=5  ->  i=i/5
		
		/*
		 * Stampa prvih 50 prirodnih brojeva u jednom redu
		 */
		for(int i=1;i<=50;i++) {
			System.out.print(i+", ");
		}

	}

}
